mapper.select:
SELECT ID, FIRST_NAME, LAST_NAME, PHONE_NUMBER, LICENSE_ID
FROM Drivers 
WHERE LICENSE_ID = #{licenseId}

mapper.update:
UPDATE Driver
SET FIRST_NAME = #{firstName}, LAST_NAME = #{lastName}, PHONE_NUMBER = #{phoneNumber}, LICENSE_ID = #{licenseId}, USER_TYPE = #{userType}
WHERE ID = #{id}
